import React, { useState, useRef } from 'react'
import './video.css';
import VideoFooter from './VideoFooter';
function Video({videoSrc}) {
    const [playing, setPlaying] = useState(false);
    const videoRef = useRef(null);
    const clickHandler = e => {
        if(playing){
            videoRef.current.pause();
            setPlaying(false)
        }else{
            videoRef.current.play();
            setPlaying(true);
        }
    }
    return (
        <div className="video">
            <video className="videoPlayer" ref={videoRef} onClick={clickHandler} loop src={videoSrc}></video>
            <VideoFooter username={"Jigme"} description = {"This is gonna be a description"} song={Musti}/>
        </div>
    )
}

export default Video
